$('.btn-login').on('click', function(event) {
    if ($('[name="username"]').val().trim() == '') {
        swal({
            title: 'Username',
            text: 'Please Fill in the Username',
            type: 'warning',

            showConfirmButton: true
        });
    } else if ($('[name="password"]').val().trim() == '') {
        swal({
            title: 'Password',
            text: 'Please Fill in the Password',
            type: 'warning',

            showConfirmButton: true
        });
    } else {
        var formData = new FormData($('#form-login')[0]);
        var url = link_base + 'auth/autolog';
        $.ajax({
            url: url,
            type: "POST",
            data: formData,
            contentType: false,
            processData: false,
            dataType: "JSON",
            success: function(data) {
                console.log(data);
                // alert(data.type);
                if (data.type == 0) {
                    swal({

                        title: '',
                        text: data.msg,
                        type: 'success',
                        showConfirmButton: false
                    });
                    window.setTimeout(function() {
                        window.location.replace(link_base);
                    }, 1000);
                } else {
                    swal({

                        title: '',
                        text: data.msg,
                        type: 'warning',
                        showConfirmButton: true
                    });
                }
            },
            error: function(jqXHR, textStatus, errorThrown) {
                swal({
                    title: 'error Procesing',
                    text: 'Procesing fail',
                    type: 'error',

                    showConfirmButton: true
                });
            }
        });
    }

});